let currentIndex = global.get('fives.currentIndex') || 0
let currentQuiz = global.get('fives.currentQuiz')
if (!currentQuiz[currentIndex]) {
    return
}
currentQuiz[currentIndex].answer = msg.payload || 1
global.set('fives.currentQuiz', currentQuiz);
msg.payload = currentQuiz[currentIndex]
return msg;

if (!msg.payload) {
    return [null, { payload: "Description is required" }]
}

const followUp = msg.payload
const events = global.get('fives.followUps', 'persistent') || []
const currentQuestion = global.get('fives.currentQuestion')
const currentSection = global.get('fives.currentSection')
const groupId = global.get('currentStep');
const location = global.get('fives.currentLocation')
const currentInspector = global.get('fives.currentInspector')
const groups = ['sort', 'set in order', 'shine', 'standardize', 'sustain']
const section = currentSection.name

let event = {
    id: events.length,
    createdOn: Date.now(),
    createdBy: currentInspector,
    location,
    section,
    question: currentQuestion.question,
    followUp,
    closedOn: null,
    closedBy: '',
    status: "open"
}


events.push(event)
global.set('fives.followUps', events, 'persistent')

msg.payload = msg.previousPage || 'Audit'

return msg;

const results = global.get('fives.results') || global.get('fives.results', 'persistent') || [];

const TOP_SCOPE = 5

const groupedByLocation = {}
for (let index = 0; index < results.length; index++) {
    const result = results[index];
    groupedByLocation[result.location] = groupedByLocation[result.location] || {
        location: result.location,
        count: 0,
        totalQuestions: 0,
        totalScore: 0
    }
    groupedByLocation[result.location].count++
    groupedByLocation[result.location].totalQuestions += result.totalQuestions
    groupedByLocation[result.location].totalScore += result.totalScore
}

const summaryByLocation = []
for (let key in groupedByLocation) {
    if (groupedByLocation.hasOwnProperty(key)) {
        const e = groupedByLocation[key]
        const MAX_SCORE = e.totalQuestions * TOP_SCOPE
        summaryByLocation.push({
            "Location": e.location,
            "Audit Count": e.count,
            "Performance": ((e.totalScore / MAX_SCORE) * 100).toFixed(2) + '%'
        })
    }
}


msg.payload = summaryByLocation;
return msg;

// Retrieve or initialize global variables
let currentInspector = global.get('fives.currentInspector') || "Anonymous"
/** @type {Number} */
let currentIndex = global.get('fives.currentIndex') || 0
/** @type {Array<Answer>} */
let currentQuiz = global.get('fives.currentQuiz');
/** @type {Array<Question>} */
const questions = global.get("fives.questions") || []

if (!questions) {
    node.error("No questions setup", msg);
    return
}

if (!currentQuiz || msg.topic === "NewAudit") {
    // @ts-ignore
    currentQuiz = RED.util.cloneMessage(questions)
    currentIndex = 0
}

/**
 * @typedef Question
 * @property { String } section - Section Title
 * @property { String } question - Section Title
 * @property { String } imageUrl - Image to display
*/

/**
 * @typedef Answer
 * @property { Number } index - Question index
 * @property { String } section - Section Title
 * @property { String } question - Question
 * @property { Number } answer - Answer
*/



// Handle previous and next actions
if (msg.topic === "next") {
    currentIndex++
    if (!currentQuiz[currentIndex]) {
        currentIndex = currentQuiz.length - 1
    }
} else if (msg.topic === "previous") {
    currentIndex--
    if (!currentQuiz[currentIndex]) {
        currentIndex = 0
    }
}


const sectionArray = []
let sectionCount = 0
let questionsAnswered = 0
let totalScore = 0
let questionsRemaining = currentQuiz.length
for (let index = 0; index < currentQuiz.length; index++) {
    const answer = currentQuiz[index];  
    answer.index = index
    answer.answer = answer.answer || null
    let section = sectionArray.find(e => e.name === answer.section)
    if (!section) {
        section = {
            index: sectionCount,
            name: answer.section,
            answerTotal: 0,
            answerCount: 0,
            questionCount: 0,
            complete: false
        }
        sectionArray.push(section)
        sectionCount++
    }
    section.questionCount++
    if (typeof answer.answer === 'number' && answer.answer > 0) {
        questionsAnswered++
        questionsRemaining--
        totalScore += answer.answer
        section.answerTotal += answer.answer
        section.answerCount++
    }
}

sectionArray.forEach(section => {
    section.complete = section.answerCount === section.questionCount
})

const allComplete = currentQuiz.every(q => typeof q.answer === 'number' && q.answer > 0)
const lastQuestion = currentIndex === (questions.length-1)

const currentQuestion = currentQuiz[currentIndex]
const currentSection = sectionArray.find(section => currentQuestion?.section === section.name)

// Set updated global variables
global.set('fives.currentQuestion', currentQuestion);
global.set('fives.currentIndex', currentIndex);
global.set('fives.currentSection', currentSection);
global.set('fives.currentQuiz', currentQuiz);

msg.payload = currentQuestion.answer
msg.currentInspector = currentInspector
msg.currentQuiz = currentQuiz
msg.currentIndex = currentIndex
msg.currentQuestion = currentQuestion
msg.currentScore = totalScore
msg.questionCount = currentQuiz.length
msg.questionsRemaining = questionsRemaining
msg.questionsAnswered = questionsAnswered
msg.sections = sectionArray
msg.currentSection = currentSection
msg.done = msg.topic === "next" && lastQuestion && allComplete
return msg;

let events = global.get('fives.followUps') || global.get('fives.followUps', 'persistent') || []
let results = global.get('fives.results') || global.get('fives.results', 'persistent') || []
let currentLocation = msg.payload
let filteredResults = results.filter(e => e.location == currentLocation)

const MAX_SCORE = 5
const totalScore = filteredResults.reduce((acc, obj) => acc + (obj.score || 0), 0)
const averageScore = ((totalScore / filteredResults.length) / MAX_SCORE) * 100

const locationEvents = events.filter(item => item.location == currentLocation)
const openEvents = locationEvents.filter(e => e.status === "open")
// node.warn({ currentLocation, events, locationEvents, openEvents, totalScore , averageScore})
msg.payload = {}
msg.payload.avgScore = averageScore
msg.payload.openEvents = openEvents.length
msg.payload.closedEvents = locationEvents.length - openEvents.length


return msg;

const events = global.get('fives.followUps','persistent') || []
const locale = global.get("fives.locale.userLocale") || global.get("fives.locale.userLocales") || 'en-GB';
const tz = global.get("fives.locale.tz") || undefined
const dtFormatter = new Intl.DateTimeFormat(locale, {
    year: 'numeric',
    month: 'numeric',
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit',
    timeZone: tz
});

const tableData = [...events].map(e => {
    const row = {
        id: e.id,
        "Inspector": e.createdBy || 'Anonymous',
        "Raised": e.createdOn ? dtFormatter.format(e.createdOn) : '-',
        Location: e.location,
        Section: e.section || e.sectionName || '-',
        Question: e.question || '-',
        "Follow up": e.feedback || e.followUp,
        "Reviewer": e.closedBy || '',
        "Closed": e.closedOn ? dtFormatter.format(e.closedOn) : '-',
        Status: e.status== "open" ? "" : ""
    }
    return row
})


msg.payload = tableData
return msg;

