let results = global.get('fives.results', 'persistent') || []
const TOP_SCORE = 5

const sections = results.map((e, i) => {
    const location = e.location
    const sections = e.sections?.map(s => {
        return {
            location,
            section: s.name,
            score: s.answerTotal / s.questionCount
        }
    }) || []
    return sections
})

const flat = sections.flat()
const grouped = {}

flat.forEach(item => {
    const { location, section, score } = item
    const key = `${location}-${section}`
    if (!grouped[key]) {
        grouped[key] = { location, section, totalScore: 0, count: 0 }
    }
    grouped[key].totalScore += score
    grouped[key].count += 1
})

const averaged = Object.values(grouped).map(group => {
    return {
        location: group.location,
        section: group.section,
        score: (((group.totalScore / group.count) / TOP_SCORE) * 100)
    }
})

msg.payload = averaged
return msg

const events = global.get('fives.followUps', 'persistent') || []
const reviewer = global.get('fives.actionReviewer') || ''
const selectedEvent = global.get('fives.selectedEvent') || ''

if (!reviewer) {
    return [null, { payload: "Please enter a User Name" }]
}
if (!selectedEvent) {
    return [null, { payload: "Please select an Event" }]
}

let id = +selectedEvent.id

let event = events.find(event => event.id === id)
if (event) {
    event.status = "closed"
    event.closedBy = reviewer
    event.closedOn = Date.now()
}
node.warn({ _: "event review", event, events });

global.set('fives.followUps', events, 'persistent')

return msg;

let results = global.get('fives.results') || global.get('fives.results', 'persistent') || []
let currentLocation = global.get('fives.currentLocation') || global.get('fives.newLocation')
if (msg.topic === 'change-location' && msg.payload) {
    currentLocation = msg.payload
}
let filteredResults = results.filter(e => e.location == currentLocation)
let lastResult = filteredResults.slice(-1)[0]

let events = global.get('fives.followUps') || global.get('fives.followUps', 'persistent') || []
const locationEvents = events.filter(item => item.location == currentLocation)
const openEvents = locationEvents.filter(e => e.status === "open")

msg.payload = {}
msg.payload.lastAuditDate = lastResult?.datetime || null
msg.payload.lastAuditScore = ((lastResult?.score || 0) / 5) * 100
msg.payload.auditCount = filteredResults.length
msg.payload.openEvents = openEvents.length
msg.payload.closedEvents = locationEvents.length - openEvents.length

return msg;

