const asyncGlobalGet = (name, store = "memory") => {
    return new Promise((resolve, reject) => {
        global.get(name, store, (err, result) => {
            if (err) {
                node.warn(err);
                return reject(err)
            }
            resolve(result)
        })
    })
}

const asyncGlobalSet = (name, value, store = "memory") => {
    return new Promise((resolve, reject) => {
        global.set(name, value, store, (err) => {
            if (err) {
                node.warn(err);
                return reject(err)
            }
            resolve()
        })
    })
}

let newCurrentPage = msg.name
let currentPage = await asyncGlobalGet("fives.currentPage")
// node.warn({_:"Read fives.currentPage from context", currentPage});

await asyncGlobalSet('fives.currentPage', newCurrentPage)
// node.warn({_:"Set fives.currentPage in context", newCurrentPage});
