const toPercent = (v, d=2) => (v * 100).toFixed(2)
const secondsToHumanReadableDuration = (seconds) => {
    const numSeconds = +seconds
    if (isNaN(numSeconds)) {
        return 'NaN'
    }
    const h = Math.floor(numSeconds / 3600)
    const m = Math.floor((numSeconds % 3600) / 60)
    const s = numSeconds % 60
    const parts = []
    if (h > 0) parts.push(`${h.toFixed(0)}h`)
    if (m > 0) parts.push(`${m.toFixed(0)}m`)
    if (s > 0 || parts.length === 0) parts.push(`${s.toFixed(0)}s`)
    return parts.join(' ')
}

