function getRandomInt(min, max) {
    return Math.floor(Math.random() * (max - min + 1)) + min;
}
function getRandomIntNear(target, minusPercent = 0.0, plusPercent = 0.0) {
    const min = Math.floor(target - (target * minusPercent))
    const max = Math.ceil(target + (target * plusPercent))
    return Math.floor(Math.random() * (max - min + 1)) + min;
}
function getRandomElement(arr) {
