function getWeekNumber(d) {
    d = new Date(Date.UTC(d.getFullYear(), d.getMonth(), d.getDate()));
    d.setUTCDate(d.getUTCDate() + 4 - (d.getUTCDay()));
    const yearStart = new Date(Date.UTC(d.getUTCFullYear(), 0, 1));
    const weekNo = Math.ceil((((d - yearStart.valueOf()) / 86400000) + 1) / 7);
    return {
        year: d.getUTCFullYear(), week: weekNo
    }
}

function generateStops(startTime, shiftMins, { minStops = 1, maxStops = 5, minDurationMins = 1, maxDurationMins = 30 } = {}) {
    if (!(startTime instanceof Date)) {
        throw new Error("startTime must be a Date object");
    }
    if (!shiftMins || shiftMins <= 0) {
        throw new Error("shiftMins must be a positive number");
    }
    minDurationMins = minDurationMins || 1
    let previousStopTime = new Date(startTime);
    const endTime = new Date(startTime.getTime() + shiftMins * 60 * 1000); // Convert shiftMins to milliseconds
    const numStops = getRandomInt(minStops, maxStops);
    const totalAvailableMilliseconds = endTime.getTime() - startTime.getTime();
    const exactSpacing = (totalAvailableMilliseconds - maxDurationMins) / (numStops + 1)
    const stops = [];
    for (let i = 0; i < numStops; i++) {
        const spacing = getRandomIntNear(exactSpacing * (i + 1), 0.30, 0.10)
        const stopStartTime = new Date(startTime.getTime() + spacing);
        stopStartTime.setMilliseconds(0)
        if (stopStartTime <= previousStopTime) {
            continue // Skip this stop if it is before the previous stop
        }
        const stopDuration = getRandomInt(minDurationMins, maxDurationMins);
        const stopDurationSecs = (stopDuration * 60) + (getRandomInt(0,59))
        const stopEndTime = new Date(stopStartTime.getTime() + stopDurationSecs * 1000);
        if (stopEndTime > endTime) {
            break // Skip this stop if it goes beyond the end time
        }

        stops.push({
            startTime: stopStartTime, // Create a new Date object to avoid mutation
            endTime: stopEndTime,     // Create a new Date object to avoid mutation
            durationSeconds: stopDurationSecs,
        });

        previousStopTime = new Date(stopEndTime); // Update for the next stop
    }
    return stops;
}

