let tableData = global.get('bp-op-term.data') || [];

const productInfo = global.get("bp-op-term.productInfo")
const productId = productInfo['product-id'];
const productTime = productInfo['currentTime'];
const productStartTime = productInfo.productionStart
const productEndTime = Date.now()
const duration = millisecondsToTimeString(productEndTime - productStartTime)


// Get the last entry from each interruption array
const interruptions = global.get('bp-op-term.interuptions') || {}
const interruptionsLookup = {}

for (const key in interruptions) {
    const interruption = interruptions[key]
    if (interruption?.millis > 0) {
        const {days,hours,minutes,seconds,milliseconds} = interruption.time
        const totalHours = (days * 24) + hours
        interruptionsLookup[key] = `${totalHours}h ${minutes}m ${seconds}s`
    }
}


// Get the number of defect and good parts
let badParts = +global.get("bp-op-term.badParts") || 0
let goodParts = +global.get("bp-op-term.goodParts") || 0
if(isNaN(badParts)) { badParts = '-' }
if(isNaN(goodParts)) { goodParts = '-' }


// Construct the payload
let payload = {
    "Product": `${productId}`,
    "Production Start Time": `${productTime}`,
    "Production Duration": duration,
    "Break Time": interruptionsLookup.break || '-',
    "Change-Over Time": interruptionsLookup.changeover || '-',
    "Machine Fault Time": interruptionsLookup.fault || '-',
    "Waiting Materials Time": interruptionsLookup.waiting || '-',
    "Exit Blocked Time": interruptionsLookup.blocked || '-',
    "Maintenance Interruption": interruptionsLookup.maintenance || '-',
    "Good Parts": goodParts,
    "Defect Parts": badParts
};

tableData.push(payload);
global.set('bp-op-term.data', tableData);

msg.payload = tableData
return msg;

