// Generate a single product with unique dummy data
let i = Math.floor(Math.random() * 200) + 1;  // Random index for variety
let timestamp = Date.now();  // Current time in milliseconds
let instanceName = env.get("FF_INSTANCE_NAME")

let product = {
    productID: `P${timestamp}`,  // Unique ID based on timestamp
    productName: `Product-${i}`,
    quantity: Math.floor(Math.random() * 500) + 1,  // Random quantity between 1 and 500
    location: `Factory ${String.fromCharCode(65 + (i % 3))}`,  // Factory A, B, or C
    productionDate: new Date().toISOString(),
    status: i % 2 === 0 ? "In Production" : "Completed"  // Alternating status
};

// Set the payload to the single product
msg.payload = product;

// Set the url 
msg.url = `https://${instanceName}.flowfuse.cloud/api/manufacturing/products/`
return msg;

if (context.get('isRunning')) {
    node.warn("Test already running, skipping...");
    return null;
}

context.set('isRunning', true);

let load = msg.payload.Load;
let numTriggers = 0;
let delay = 0;

switch (load) {
    case "Low":
        numTriggers = 30;
        delay = 2000;
        break;
    case "Medium":
        numTriggers = 120;
        delay = 500;
        break;
    case "High":
        numTriggers = 300;
        delay = 200;
        break;
    case "Extreme":
        numTriggers = 600;
        delay = 100;
        break;
    default:
        node.warn("Unknown load level");
        context.set('isRunning', false);
        return null;
}

node.status({ fill: "blue", shape: "dot", text: `Testing` });

for (let count = 0; count < numTriggers; count++) {
    setTimeout(() => {
        let clonedMsg = RED.util.cloneMessage(msg);
        clonedMsg.payload.triggerCount = count + 1;
        node.send(clonedMsg); // Use node.send

        if (count + 1 === numTriggers) {
            node.status({ fill: "green", shape: "ring", text: "Completed" });
            context.set('isRunning', false); // Ensure it's reset
        }
    }, count * delay);
}

return null;

