const products = global.get("products") || [];
const productId = msg.req.params.id;
const newQuantity = msg.payload

// Find and update the product
for (let i = 0; i < products.length; i++) {
    if (products[i].productID == productId) {
        products[i].quantity = newQuantity;
        break;
    }
}

// Save the updated products back to global context
global.set("products", products);

return msg;

const products = global.get("products") || [];
const productId = msg.req.params.id;

// Find and remove the product by ID
for (let i = 0; i < products.length; i++) {
    if (products[i].productID == productId) {
        products.splice(i, 1); // Remove the matched product
        break;
    }
}

// Save the updated products back to global context
global.set("products", products);

return msg;
